mapper.select:
SELECT ord_code, mbsp_id, ord_name, ord_addr_zipcode, ord_addr_basic, ord_addr_detail, ord_tel, ord_price, ord_desc, ord_regdate FROM ( SELECT /*+ INDEX_DESC(order_tbl pk_ord_code) */ ROWNUM AS rn, ord_code, mbsp_id, ord_name, ord_addr_zipcode, ord_addr_basic, ord_addr_detail, ord_tel, ord_price, ord_desc, ord_regdate FROM order_tbl WHERE
ROWNUM <= (#{pageNum} * #{amount})
) WHERE rn > (#{pageNum} - 1 ) * #{amount}

mapper.delete:
DELETE FROM
	ordetail_tbl
WHERE
	ord_code = #{ord_code}
AND
	pro_num = #{pro_num}
